import React, { Component } from "react";
import { Consumer } from "../../context";
import axios from "axios";
import queryString from "query-string";
import Moment from "react-moment";
import {
  FacebookIcon,
  EmailShareButton,
  FacebookShareButton,
  TwitterShareButton,
  TwitterIcon,
  EmailIcon,
} from "react-share";
import Bookmark from "../Navbars/Bookmark";

export default class Details extends Component {
  constructor(props) {
    super(props);
  }
  state = {
    detailNews: {},
  };

  componentDidMount() {
    const value = queryString.parse(this.props.location.search);
    const id = value.id;
    axios
      .get(`/article?id=${id}`)
      .then((res) => {
        console.log(res.data);
        this.setState({
          detailNews: res.data,
        });
      })
      .catch((err) => console.log(err));
  }
  render() {
    return (
      <div className="card">
        <h5 className="card-title">{this.state.detailNews.detailTitle}</h5>
        <div className="d-flex bd-highlight mb-3">
          <div className="p-2 bd-highlight">
            <Moment format="MM/DD/YYYY">
              {this.state.detailNews.detailDate}
            </Moment>
            <br />
          </div>
          <div className="ml-auto p-2 bd-highlight">
            {/* -------------------------------------share----------------------------------------- */}
            {/* <ul class="list-group list-group-horizontal-md border-0">
              <li class="list-group-item border-0">
                <FacebookShareButton
                  url={this.state.detailNews.detailImg}
                  hashtag="#CSCI_571_NewsApp"
                  className="button"
                >
                  <FacebookIcon size={32} round={true} />
                </FacebookShareButton>
              </li>
              <li class="list-group-item border-0">
                <TwitterShareButton
                  url={this.state.detailNews.detailUrl}
                  hashtags={["CSCI_571_NewsApp"]}
                  className="button"
                >
                  <TwitterIcon size={32} round={true} />
                </TwitterShareButton>
              </li>
              <li class="list-group-item border-0">
                <EmailShareButton
                  url={this.state.detailNews.detailUrl}
                  subject="#CSCI_571_NewsApp"
                  body=""
                  separator=""
                >
                  <EmailIcon size={32} round={true} />
                </EmailShareButton>
              </li>
              <li class="list-group-item border-0">
                <Bookmark />
              </li>
            </ul> */}
            <div class="container px-lg-5">
              <div class="row mx-lg-n5">
                <div class="col py-3 px-lg-5 border bg-light">
                  Custom column padding
                </div>
                <div class="col py-3 px-lg-5 border bg-light">
                  Custom column padding
                </div>
              </div>
            </div>
          </div>
        </div>
        <img
          src={this.state.detailNews.detailImg}
          className="card-img-top"
          alt="..."
        />
        <div className="card-body">
          <p className="card-text">
            Some quick example text to build on the card title and make up the
            bulk of the card's content.
          </p>
          <a href="#" className="btn btn-primary">
            Go somewhere
          </a>
        </div>
      </div>
    );
  }
}
