import React, { Component } from "react";
import { Consumer } from "../../context";
import axios from "axios";
import queryString from "query-string";
import Moment from "react-moment";
import {
  FacebookIcon,
  EmailShareButton,
  FacebookShareButton,
  TwitterShareButton,
  TwitterIcon,
  EmailIcon,
} from "react-share";
import Bookmark from "../Navbars/Bookmark";
import ReadMore from "./readMore/ReadMore";

export default class Details extends Component {
  constructor(props) {
    super(props);
  }
  state = {
    detailNews: {},
    detailDescription: "",
  };

  componentDidMount() {
    const value = queryString.parse(this.props.location.search);
    const id = value.id;

    axios
      .get(`/article?id=${id}`)
      .then((res) => {
        console.log(res.data);
        this.setState({
          detailNews: res.data,
          detailDescription: res.data.detailDescription,
        });
      })
      .catch((err) => console.log(err));
    var lines = res.data.detailDescription.split(/\r\n|\r|\n/).length;
  }
  getWrapperWidth() {
    if (this.wrapper) {
      console.log(
        "get wrapper width",
        window.getComputedStyle(this.wrapper).getPropertyValue("width")
      );
    } else {
      console.log("get wrapper - no wrapper");
    }
  }
  render() {
    return (
      <div className="card">
        <h5 className="card-title">{this.state.detailNews.detailTitle}</h5>
        <div className="d-flex bd-highlight mb-3">
          <div className="p-2 bd-highlight">
            <Moment format="MM/DD/YYYY">
              {this.state.detailNews.detailDate}
            </Moment>
            <br />
          </div>
          <div className="ml-auto p-2 bd-highlight">
            {/* -------------------------------------share----------------------------------------- */}
            <div className="container px-lg-5 border-0">
              <div className="row mx-lg-n5">
                <div className="col py-3 px-lg-3 border border-0">
                  <FacebookShareButton
                    url={this.state.detailNews.detailImg}
                    hashtag="#CSCI_571_NewsApp"
                    className="button"
                    data-toggle="tooltip"
                    data-placement="top"
                    title="Facebook"
                  >
                    <FacebookIcon size={32} round={true} />
                  </FacebookShareButton>{" "}
                </div>
                <div
                  className="col py-3 px-lg-3 border  border-0"
                  data-toggle="tooltip"
                  data-placement="top"
                  title="Twitter"
                >
                  <TwitterShareButton
                    url={this.state.detailNews.detailUrl}
                    hashtags={["CSCI_571_NewsApp"]}
                    className="button"
                  >
                    <TwitterIcon size={32} round={true} />
                  </TwitterShareButton>{" "}
                </div>
                <div className="col py-3 px-lg-3 border  border-0">
                  <EmailShareButton
                    url={this.state.detailNews.detailUrl}
                    subject="#CSCI_571_NewsApp"
                    body=""
                    separator=""
                    data-toggle="tooltip"
                    data-placement="top"
                    title="Email"
                  >
                    <EmailIcon size={32} round={true} />
                  </EmailShareButton>
                </div>
                <div className="col py-3 px-lg-5 border  border-0">
                  <Bookmark />
                </div>
              </div>
            </div>
          </div>
        </div>
        <img
          src={this.state.detailNews.detailImg}
          className="card-img-top"
          alt="..."
        />
        <div className="card-body">
          <ReadMore
            text={this.state.detailDescription}
            numberOfLines={30}
            lineHeight={1.4}
            showLessButton={true}
            onContentChange={this.getWrapperWidth}
          />
        </div>
      </div>
    );
  }
}
