import React, { Component } from "react";
import { Card } from "react-bootstrap";
import Moment from "react-moment";
import SectionTag from "../News/SectionTag";
import { Link } from "react-router-dom";
import { MdShare } from "react-icons/md";
import { Modal, Button } from "antd";
import "antd/dist/antd.css";
import {
  FacebookIcon,
  EmailShareButton,
  FacebookShareButton,
  TwitterShareButton,
  TwitterIcon,
  EmailIcon,
} from "react-share";

export default class FavoritesBlock extends Component {
  render() {
    const {
      detailId,
      title,
      description,
      subDescription,
      detailUrl,
      detailDate,
      detailSection,
      detailImg,
    } = this.props.favoritesItem;
    // console.log(this.props.favoritesItem);
    // console.log(detailSection);
    return (
      <Link to={`/article?id=${detailId}`}>
        <div className="justify-content-center">
          <div className="col mt-3 mb-3">
            <Card style={{ width: "18rem" }}>
              <Card.Body>
                <Card.Title>{title}</Card.Title>
                <Button
                  type="link"
                  onClick={(e) => {
                    this.setModal2Visible(true);
                    e.preventDefault();
                    e.stopPropagation();
                  }}
                >
                  <MdShare />
                </Button>
                <div>
                  <Modal
                    title={this.props.title}
                    centered
                    width="300px"
                    footer=" "
                    visible={this.state.modal2Visible}
                    onOk={(e) => {
                      this.setModal2Visible(false);
                      e.preventDefault();
                      e.stopPropagation();
                    }}
                    onCancel={(e) => {
                      this.setModal2Visible(false);
                      e.preventDefault();
                      e.stopPropagation();
                    }}
                  >
                    <h5 className="text-center">Share via</h5>
                    {/* -------------------------------------share----------------------------------------- */}
                    <div className="container">
                      <div className="row">
                        <div className="col">
                          <FacebookShareButton
                            url={this.props.detailUrl}
                            hashtag="#CSCI_571_NewsApp"
                            className="button"
                            data-toggle="tooltip"
                            data-placement="top"
                            title="Facebook"
                          >
                            <FacebookIcon size={32} round={true} />
                          </FacebookShareButton>{" "}
                        </div>
                        <div className="col">
                          <div
                            data-toggle="tooltip"
                            data-placement="top"
                            title="Twitter"
                          >
                            <TwitterShareButton
                              url={this.props.detailUrl}
                              hashtags={["CSCI_571_NewsApp"]}
                              className="button"
                            >
                              <TwitterIcon size={32} round={true} />
                            </TwitterShareButton>{" "}
                          </div>
                        </div>
                        <div className="col">
                          <EmailShareButton
                            url={this.props.detailUrl}
                            subject="#CSCI_571_NewsApp"
                            body=""
                            separator=""
                            data-toggle="tooltip"
                            data-placement="top"
                            title="Email"
                          >
                            <EmailIcon size={32} round={true} />
                          </EmailShareButton>
                        </div>
                      </div>
                    </div>
                  </Modal>
                </div>
                <Card.Img variant="top" src={detailImg} />
                <Card.Text>
                  <div className="d-flex bd-highlight mb-3">
                    <div className="p-2 bd-highlight">
                      <Moment format="MM/DD/YYYY">{detailDate}</Moment>
                      <br />
                    </div>
                    <div className="ml-auto p-2 bd-highlight">
                      <SectionTag sectionName={detailSection} />
                    </div>
                  </div>
                </Card.Text>
              </Card.Body>
            </Card>
          </div>
        </div>
      </Link>
    );
  }
}
