import React, { Component } from "react";
import { Consumer } from "../../context";
import axios from "axios";
import queryString from "query-string";
import Moment from "react-moment";
import {
  FacebookIcon,
  EmailShareButton,
  FacebookShareButton,
  TwitterShareButton,
  TwitterIcon,
  EmailIcon,
} from "react-share";
import Bookmark from "../Navbars/Bookmark";
// import ReadMoreText from "read-more-less-react";
// import "read-more-less-react/dist/index.css";
// import ReadMoreAndLess from "react-read-more-less";
// import ReadMoreReact from "read-more-react";
// import { ReadMore } from "react-read-more";
// import ReadMore from "react-readmore";
// import ClampLines from "react-clamp-lines";
// import LinesEllipsis from "react-lines-ellipsis";
import ReadMore from "@jamespotz/react-simple-readmore";

export default class Details extends Component {
  constructor(props) {
    super(props);
  }
  state = {
    detailNews: {},
    detailDescription: "",
  };

  componentDidMount() {
    const value = queryString.parse(this.props.location.search);
    const id = value.id;

    axios
      .get(`/article?id=${id}`)
      .then((res) => {
        console.log(res.data);
        this.setState({
          detailNews: res.data,
          detailDescription: res.data.detailDescription,
        });
      })
      .catch((err) => console.log(err));
  }
  //   getWrapperWidth() {
  //     if (this.wrapper) {
  //       console.log(
  //         "get wrapper width",
  //         window.getComputedStyle(this.wrapper).getPropertyValue("width")
  //       );
  //     } else {
  //       console.log("get wrapper - no wrapper");
  //     }
  //   }
  render() {
    return (
      <div>
        <div className="card">
          <h5 className="card-title">{this.state.detailNews.detailTitle}</h5>
          <div className="d-flex bd-highlight mb-3">
            <div className="p-2 bd-highlight">
              <Moment format="MM/DD/YYYY">
                {this.state.detailNews.detailDate}
              </Moment>
              <br />
            </div>
            <div className="ml-auto p-2 bd-highlight">
              {/* -------------------------------------share----------------------------------------- */}
              <div className="container px-lg-5 border-0">
                <div className="row mx-lg-n5">
                  <div className="col py-3 px-lg-3 border border-0">
                    <FacebookShareButton
                      url={this.state.detailNews.detailImg}
                      hashtag="#CSCI_571_NewsApp"
                      className="button"
                      data-toggle="tooltip"
                      data-placement="top"
                      title="Facebook"
                    >
                      <FacebookIcon size={32} round={true} />
                    </FacebookShareButton>{" "}
                  </div>
                  <div
                    className="col py-3 px-lg-3 border  border-0"
                    data-toggle="tooltip"
                    data-placement="top"
                    title="Twitter"
                  >
                    <TwitterShareButton
                      url={this.state.detailNews.detailUrl}
                      hashtags={["CSCI_571_NewsApp"]}
                      className="button"
                    >
                      <TwitterIcon size={32} round={true} />
                    </TwitterShareButton>{" "}
                  </div>
                  <div className="col py-3 px-lg-3 border  border-0">
                    <EmailShareButton
                      url={this.state.detailNews.detailUrl}
                      subject="#CSCI_571_NewsApp"
                      body=""
                      separator=""
                      data-toggle="tooltip"
                      data-placement="top"
                      title="Email"
                    >
                      <EmailIcon size={32} round={true} />
                    </EmailShareButton>
                  </div>
                  <div className="col py-3 px-lg-5 border  border-0">
                    <Bookmark />
                  </div>
                </div>
              </div>
            </div>
          </div>
          <img
            src={this.state.detailNews.detailImg}
            className="card-img-top"
            alt="..."
          />
          <div className="card-body">
            {/* https://www.npmjs.com/package/read-more-less-react 
            <ReadMoreText
              lines={5}
              text={this.state.detailDescription}
              readMoreText={"⌄"}
              readLessText={"⌃"}
            /> */}
            {/* <ReadMoreAndLess
              ref={this.ReadMore}
              className="read-more-content"
              charLimit={250}
              readMoreText="Read more"
              readLessText="Read less"
            >
              {this.state.detailDescription}
            </ReadMoreAndLess> */}
            {/* <ReadMoreReact text={this.state.detailDescription} /> */}
            {/* <ReadMore minHeight="52px">
              <p>
                Some <strong>Content</strong>. Obviously, make this more than
                52px high worth of content
              </p>
            </ReadMore> */}
            {/* <LinesEllipsis
              text="long long textlong long textlong long textlong long textlong long textlong long textlong long textlong long textlong long textlong long textlong long textlong long textlong long text"
              maxLine="3"
              ellipsis="..."
              trimRight
              basedOn="letters"
            />{" "} */}
            <div style={{ width: 400 }}>
              <ReadMore
                minHeight={150}
                defaultShownOnLess={
                  <p
                    dangerouslySetInnerHTML={{
                      __html: truncate(text, { length: 500 }),
                    }}
                  />
                }
                preset="wobbly"
              >
                <p
                  dangerouslySetInnerHTML={{
                    __html: text,
                  }}
                />
              </ReadMore>
            </div>
            {/* sssssssssssss */}
          </div>
        </div>
        <p>123</p>
      </div>
    );
  }
}
