    //       });
    //       // console.log(this.state.news_list);
    //     })
    //     .catch((err) => console.log(err));
    // }
  }
  render() {
    return (
      <Context.Provider value={this.state}>
        {this.props.children}
      </Context.Provider>
    );
  }
}

export const Consumer = Context.Consumer;
