import React, { Component } from "react";
import { CardDeck } from "react-bootstrap";
import FavoritesBlock from "./FavoritesBlock";

export default class FavoritesPage extends Component {
  constructor(props) {
    super(props);

    this.state = {
      favoritesList: [],
    };
  }

  componentDidMount() {
    if (localStorage.getItem("jfVersion") != null) {
      localStorage.removeItem("jfVersion");
    }

    for (let i = 0; i < localStorage.length; i++) {
      if (this.state.favoritesList.contain)
        this.setState({
          favoritesList: [
            ...this.state.favoritesList,
            JSON.parse(localStorage.getItem(localStorage.key(i))),
          ],
        });
    }
  }

  render() {
    // return (
    //   <CardDeck>
    //     {localStorage.map((item) => {
    //       <FavoritesBlock
    //         key={localStorage.getItem(item.key)}
    //         favoritesItem={JSON.parse(localStorage.getItem(item.value))}
    //       />;
    //     })}
    //   </CardDeck>
    // );

    const { favoritesList } = this.state;
    if (favoritesList.length != 0) {
      return (
        <CardDeck>
          {favoritesList.map((item) => (
            <FavoritesBlock key={item.id} favoritesItem={item} />
          ))}
        </CardDeck>
      );
    } else {
      return (
        <div className="text-center">
          <div className="spinner-border" role="status">
            <span className="sr-only">Loading...</span>
          </div>
        </div>
      );
    }
  }
}
