  promiseOptions = (inputValue) => {
    if (!inputValue) {
      return Promise.resolve({ results: [] });
    }
    return fetch(
      `https://api.cognitive.microsoft.com/bing/v7.0/Suggestions?q=${encodeURIComponent(
        inputValue
      )}`,
      {
        headers: {
          "Ocp-Apim-Subscription-Key": "fd0ef54849aa44c890942a2eb8866194",
        },
      }
    )
      .then((response) => {
        console.log(encodeURIComponent(inputValue));
        return response.json();
      })
      .then((data) => {
        const formatted = data.suggestionGroups[0].searchSuggestions.map(
          (l) => ({
            value: l.displayText,
            label: l.displayText,
          })
        );
        console.log(formatted);
        return { formatted };
      });
  };

  render() {
    return <AsyncSelect loadOptions={this.promiseOptions} />;
  }
}
