import React from "react";
import "antd/dist/antd.css";
import { Tag } from "antd";

export default function SectionTag(props) {
  switch (props.sectionName) {
    case "world":
      return <Tag color="#8A2BE2">{props.sectionName}</Tag>;
      break;
    case "politics":
      return <Tag color="#8A2BE2">{props.sectionName}</Tag>;

      break;
    case "business":
      return (
        <button type="button" style={{ background: "#1E90FF" }}>
          {props.sectionName}
        </button>
      );
      break;
    case "technology":
      return (
        <button type="button" style={{ background: "#7CFC00" }}>
          {props.sectionName}
        </button>
      );
      break;

    case "sports":
      return (
        <button type="button" style={{ background: "#FFD700" }}>
          {props.sectionName}
        </button>
      );
      break;

    case "guardian":
      return (
        <button type="button" style={{ background: "#000080" }}>
          {props.sectionName}
        </button>
      );
      break;

    case "nytimes":
      return (
        <button type="button" style={{ background: "#A9A9A9" }}>
          {props.sectionName}
        </button>
      );
      break;
    default:
      return (
        <button type="button" style={{ background: "#696969" }}>
          HEALTH
        </button>
      );
  }
}
