import React, { Component } from "react";
import Moment from "react-moment";
import { Link } from "react-router-dom";
import { Nav } from "react-bootstrap";

export default class NewsBlock extends Component {
  constructor(props) {
    super(props);
  }
  render() {
    return (
      <Nav.Link>
        <div className="card mb-3 mt-3 ml-3">
          <div className="row no-gutters">
            <div className="col-md-4 ">
              <div className="mb-3 mt-3 ml-3 mr-3">
                <img src={this.props.img} className="card-img" alt="..." />
              </div>
            </div>
            <div className="col-md-8">
              <div className="card-body">
                <h5 className="card-title">{this.props.title}</h5>
                <p className="card-text">{this.props.subDescription}</p>
                <div className="d-flex bd-highlight mb-3">
                  <div className="p-2 bd-highlight">
                    <Moment format="MM/DD/YYYY">{this.props.date}</Moment>
                  </div>
                  <div className="ml-auto p-2 bd-highlight">
                    {this.props.section}
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </Nav.Link>
    );
  }
}
