import React from "react";
import "antd/dist/antd.css";
import { Tag } from "antd";

export default function SectionTag(props) {
  switch (props.sectionName) {
    case "world":
      return (
        <Tag color="#8A2BE2" className="text-uppercase font-weight-bold">
          {props.sectionName}
        </Tag>
      );
      break;
    case "World news":
      return (
        <Tag color="#8A2BE2" className="text-uppercase font-weight-bold">
          WORLD
        </Tag>
      );
      break;
    case "politics":
      return (
        <Tag color="#228B22" className="text-uppercase font-weight-bold">
          {props.sectionName}
        </Tag>
      );
      break;
    case "Politics":
      return (
        <Tag color="#228B22" className="text-uppercase font-weight-bold">
          {props.sectionName}
        </Tag>
      );
      break;
    case "business":
      return (
        <Tag color="#1E90FF" className="text-uppercase font-weight-bold">
          {props.sectionName}
        </Tag>
      );
      break;
    case "Business":
      return (
        <Tag color="#1E90FF" className="text-uppercase font-weight-bold">
          {props.sectionName}
        </Tag>
      );
      break;
    case "technology":
      return (
        <Tag color="#7CFC00" className="text-uppercase font-weight-bold">
          {props.sectionName}
        </Tag>
      );
      break;

    case "sport":
      return (
        <Tag color="#FFD700" className="text-uppercase font-weight-bold">
          {props.sectionName}
        </Tag>
      );
      break;

    case "guardian":
      return (
        <Tag color="#000080" className="text-uppercase font-weight-bold">
          {props.sectionName}
        </Tag>
      );
      break;

    case "nytimes":
      return (
        <Tag color="#A9A9A9" className="text-uppercase font-weight-bold">
          {props.sectionName}
        </Tag>
      );
      break;

    default:
      return (
        <Tag color="#696969" className="align-self-center font-weight-bold">
          HEALTH
        </Tag>
      );
      break;
  }
}
