    //     })}
    //   </CardDeck>
    // );
    console.log(localStorage.length);
    const localArr = Object.entries(localStorage);
    console.log(localStorage);

    if (localArr.length != 0) {
      return (
        <div>
          <CardDeck>
            {localArr.map((item) => (
              <FavoritesBlock
                key={localArr.getItem(item.key)}
                favoritesItem={JSON.parse(localArr.getItem(item.value))}
              />
            ))}
          </CardDeck>
        </div>
      );
    } else {
      return (
        <div className="text-center">
          <div className="spinner-border" role="status">
            <span className="sr-only">Loading...</span>
          </div>
        </div>
      );
    }

