import React, { Component } from "react";
import {
  FacebookIcon,
  EmailShareButton,
  FacebookShareButton,
  TwitterShareButton,
  TwitterIcon,
  EmailIcon,
} from "react-share";

export default class Share extends Component {
  constructor(props) {
    super(props);
    this.props = detailNews;
  }
  render() {
    return (
      <ul class="list-group list-group-horizontal-md border-0">
        <li class="list-group-item border-0">
          <FacebookShareButton
            url={this.props.detailNews.detailImg}
            hashtag="#CSCI_571_NewsApp"
            className="button"
          >
            <FacebookIcon size={32} round={true} />
          </FacebookShareButton>
        </li>
        <li class="list-group-item border-0">
          <TwitterShareButton
            url={this.props.detailNews.detailUrl}
            hashtags={["CSCI_571_NewsApp"]}
            className="button"
          >
            <TwitterIcon size={32} round={true} />
          </TwitterShareButton>
        </li>
        <li class="list-group-item border-0">
          <EmailShareButton
            url={this.props.detailNews.detailUrl}
            subject="#CSCI_571_NewsApp"
            body=""
            separator=""
          >
            <EmailIcon size={32} round={true} />
          </EmailShareButton>
        </li>
      </ul>
    );
  }
}
