import { Consumer } from "../../context";
import axios from "axios";
import queryString from "query-string";

export default class Details extends Component {
  constructor(props) {
    super(props);
  }
  state = {
    detailNews: {
      detailUrl: "",
      detailImg: "",
      detailDescription: "",
    },
  };

  componentDidMount() {
