import React, { Component } from "react";
import { Form, FormControl, Nav, Button, Navbar } from "react-bootstrap";
import Toggle from "./Toggle";
import Bookmark from "./Bookmark";
// import Search from "./Search";
import SearchSelect from "./SearchSelect";
import { FaRegBookmark, FaBookmark } from "react-icons/fa";
import InBookMark from "./InBookMark";
import OutBookMark from "./OutBookMark";

export default class Navbars extends Component {
  render() {
    let tab = window.location.pathname;
    console.log(tab);
    return (
      <Navbar bg="light" expand="lg" style={gradientStyle}>
        {/* <Search /> */}
        <SearchSelect className="mt-2" />
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="mr-auto">
            <Nav.Link href="home" style={navbarTextColor}>
              Home
            </Nav.Link>
            <Nav.Link href="world" style={navbarTextColor}>
              World
            </Nav.Link>
            <Nav.Link href="politics" style={navbarTextColor}>
              Politics
            </Nav.Link>
            <Nav.Link href="business" style={navbarTextColor}>
              Business
            </Nav.Link>
            <Nav.Link href="technology" style={navbarTextColor}>
              Technology
            </Nav.Link>
            <Nav.Link href="sports" style={navbarTextColor}>
              Sports
            </Nav.Link>
          </Nav>
          <Nav className="ml-auto">
            {/* <Nav.Link href="favorites">
              <Bookmark />
            </Nav.Link> */}
            {tab !== "/favorites" ? (
              <div>
                <div className="row">
                  <div className="col-6 col-md-2 mt-2 ml-2">
                    <Nav.Link href="favorites">
                      <OutBookMark />
                    </Nav.Link>
                  </div>
                  <div className="col-6 col-md-2 mt-3 ml-2">
                    <span style={navbarTextColor}>NYTimes</span>
                  </div>
                  <div className="col-6 col-md-2 mt-2">
                    <Nav.Link className="mt-1">
                      <Toggle />
                    </Nav.Link>
                  </div>
                  <div className="col-6 col-md-2 mt-3 ml-2">
                    <span style={navbarTextColor}>Guardian</span>
                  </div>
                </div>
              </div>
            ) : (
              <div>
                <Nav.Link href="favorites">
                  <InBookMark />
                </Nav.Link>
              </div>
            )}
          </Nav>
        </Navbar.Collapse>
      </Navbar>
    );
  }
}

const gradientStyle = {
  background: "red",
  background: "linear-gradient(to right, #152444 , #3d61b5)",
};

const navbarTextColor = {
  color: "white",
};
