import React, { Component } from "react";
import Moment from "react-moment";
import { Link } from "react-router-dom";
import { Nav, Toast, Button } from "react-bootstrap";
import { MdShare } from "react-icons/md";

export default class NewsBlock extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showA: true,
      setShowA: true,
    };
  }
  toggleShowA = () => {
    this.setState({
      showA: !this.stateshowA,
    });
  };
  render() {
    return (
      <Link
        className="mb-3 mt-3 ml-3 text-decoration-none"
        to={`/article?id=${this.props.id}`}
      >
        <div className="card ">
          <div className="row no-gutters">
            <div className="col-md-4 ">
              <div className="mb-3 mt-3 ml-3 mr-3">
                <img src={this.props.img} className="card-img" alt="..." />
              </div>
            </div>
            <div className="col-md-8">
              <div className="card-body">
                <h5 className="card-title">
                  {this.props.title}
                  {/* -------------------------------------------- */}
                  {/* share button */}
                  <Toast
                    show={this.stateshowA}
                    onClose={this.toggleShowA}
                    autohide
                  >
                    <Toast.Header>
                      <img
                        src="holder.js/20x20?text=%20"
                        className="rounded mr-2"
                        alt=""
                      />
                      <strong className="mr-auto">Bootstrap</strong>
                      <small>11 mins ago</small>
                    </Toast.Header>
                    <Toast.Body>
                      Woohoo, you're reading this text in a Toast!
                    </Toast.Body>
                  </Toast>
                  <Button onClick={this.toggleShowA}>
                    <MdShare />
                  </Button>
                  {/* ------------------------------------------------ */}
                </h5>
                <p className="card-text">{this.props.subDescription}</p>
                <div className="d-flex bd-highlight mb-3">
                  <div className="p-2 bd-highlight">
                    <Moment format="MM/DD/YYYY">{this.props.date}</Moment>
                    <br />
                  </div>
                  <div className="ml-auto p-2 bd-highlight">
                    {this.props.section}
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </Link>
    );
  }
}
