var commentId = "";
export default class Comment extends Component {
  constructor(props) {
    super(props);
    this.state = {
      commentId: "",
    };
  }

  shouldComponentUpdate(newProps, newState) {
    commentId = newProps.commentId;
    // this.setState((this.state.commentId: newProps.commentId));
    return true;
  }
  componentDidMount() {
    this.removeCommentBox = commentBox("5746809972457472-proj", {
      defaultBoxId: `${this.props.commentId}`, // the default ID to associate to the div
    });
  }

  componentWillUnmount() {
    this.removeCommentBox();
  }

  render() {
    return <div className="commentbox" />;
  }
}
